class Solution {
public:
    long long findAnswer(long long jump,long long n,int k,int last,map<array<long long,3>,int>&mp){
        if(mp.find({jump,n,last})!=mp.end())return mp[{jump,n,last}];
        long long ans=0;
        if(n>k+1)return 0;
        if(n==k){
            ans+=1;
        }
        if(last!=-1 && n>0){
            ans+=findAnswer(jump,n-1,k,-1,mp);
        }
        ans+=findAnswer(jump+1,n+(1LL<<jump),k,0,mp);
        mp[{jump,n,last}] = ans;
        return ans;
    }

    int waysToReachStair(int k) {
        map<array<long long,3>,int> mp, mp1;
        return findAnswer(0,1,k,0,mp);
    }
};
