class Solution:
    def waysToReachStair(self, k: int) -> int:



        @cache
        def t(i,jump,j):

            if i==0:j=0

            res=i==k       

            if j==0 and i+2**jump>k+1:
                return res


            elif j==0:
                return res+t(i+2**jump,jump+1,1) 


            elif j==1 and i+2**jump>k+1:  
                return res+t(i-1,jump,0)


            return res+t(i-1,jump,0)+t(i+2**jump,jump+1,1)  

        return t(1,0,1)
