class Solution {
public:
    map<tuple<int,int,bool>,int> dp;
    ll f(ll n,ll m,bool isDec){
        if(n<0 || m<0)return 0;
        if(n==0)return 1;
        if(m==0){
            if(isDec)return 0;
            if(n==1)return 1;
            return 0;
        }
        tuple<int,int,bool> t=make_tuple(n,m,isDec);
        if(dp.count(t))return dp[t];
        ll ans=f(n,m-1,false);
        if(!isDec)ans+=f(n-1,m,true);
        return dp[t]=ans;
    }
    int waysToReachStair(int k) {
        ll p=1,n=0,m=0,kk=k,ans=0;
        while(true){
            n=p-kk;
            if(m){
                if(n>3*m)break;
            }
            ans+=f(n,m,false);
            m++;
            p*=2;                                                           
        }
        return ans;
    }
};
