typedef __int128_t ll;

class Solution {
public:



    ll nck(ll n, ll k) {
        ll option1 = k; ll option2 = n - k;
        if(option1 > option2){
            swap(option1, option2);
        }

        ll ans = 1;
        for(ll i = option2 + 1; i <= n; i++){
            ans *= i;
        }
        for(ll i = 1; i <= option1; i++){
            ans /= i;
        }

        return ans;
    }

    int waysToReachStair(int k) {

        ll jumps = 0; ll distCovered = 0; ll ans = 0;
        while(true) {
            ll maxDownSteps = jumps + 1;

            if(distCovered < (k - 1)){    
                distCovered += (1LL << jumps); 
                jumps++;
                continue;
            }

            ll needDown = distCovered - (k - 1);
            if(needDown > maxDownSteps) break;

            ll possibleLocs = maxDownSteps;
            ll add = nck(possibleLocs, needDown);
            ans += add;

            distCovered += (1LL << jumps); 
            jumps++;

        }
        return ans;
    }
};
