class Solution {
public:
    map<string,int> aux;
    int rec(int i,int j,int k,int c){
        if(i>k+1){ return 0; }
        string s=to_string(i)+"  "+to_string(j)+"  "+to_string(c);
        if(aux.count(s)==0){
            aux[s]=(i==k);
            if(c){
                aux[s]+=rec(i-1,j,k,0);
            }
            aux[s]+=rec(i+(int)pow(2,j),j+1,k,1);
        }
        return aux[s];
    }
    int waysToReachStair(int k) {
        return rec(1,0,k,1);
    }
};
