class Solution:
    def waysToReachStair(self, k: int) -> int:
        memo = dict()
        def dfs(stair, jump, prev_move):
            if (stair, jump, prev_move) in memo:
                return memo[(stair, jump, prev_move)]
            if stair > k + 1:
                return 0
            if stair == k + 1 and prev_move == 0:
                return 0

            value = 0 if stair != k else 1
            if prev_move != 0:
                value += dfs(stair - 1, jump, 0)
            value += dfs(stair + 2 ** jump, jump + 1, 1)
            memo[(stair, jump, prev_move)] = value
            return value
        return dfs(1, 0, 1)
