class Solution {
    int binom(int n,int m){
        long long ret=1;
        for(int i=1;i<=m;i++){
            ret*=n-i+1;
            ret/=i;
        }
        return ret;
    }
public:
    int waysToReachStair(int k) {
        int ans=0;
        for(int j=0;j<=30;j++){
            int mx=(1<<j)-1;
            int mn=mx-(j+1);
            if(k-1<mn||k-1>mx)
                continue;
            ans+=binom(j+1,mx-(k-1));
        }
        return ans;
    }
};
