class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(current, k, used_op1, jump):
            # print(current, k, used_op1, jump)
            total = 0
            if current == k:
                total += 1
            elif current < 0:
                return 0
            if current > k + 1:
                return total
            if used_op1 == 0:
                total += dfs(current - 1, k, 1, jump)
            total += dfs(current + (2 ** jump), k, 0, jump + 1)
            return total
        return dfs(1, k, 0, 0)
