class Solution:
    def waysToReachStair(self, k: int) -> int:
        @functools.cache
        def dfs(k: int, useK: bool, step: int) -> int:
            if k == 1 and step == -1:
                return 1
            ans = 0
            if not useK:
                ans += dfs(k+1,True,step)
            if step < 0 or k == 0:
                return ans
            mask = 1 << step
            next_ = k-mask
            if next_ >= 0:
                ans += dfs(next_,False,step-1)
            return ans
        return sum(dfs(k,False,s) for s in range(-1,32))
