class Solution {
public:
    int ncr(int n,int r){
        long long x=1;

        for(int i=1;i<=r;i++){
            x=x*(n-r+i)/i;


        }
        return x;


    }
    int waysToReachStair(int k) {
        k-=1;
        long long ways=0;
        long long jump=0;
       long long i=0;
        long long down=1;

        while((jump-down)<=k){

           long long x=k-jump;
            // cout<<(i+1)<<" "<<x<<endl;
            // cout<<(ncr((i+1),abs(x)))<<endl;

            if(x<=0){
                 ways+=ncr((i+1),abs(x));


            }


            jump+=(1<<i);
            i++;
            down++;


        }

        return ways;

    }
};
