class Solution:
    def waysToReachStair(self, k: int) -> int:
        dp = {}
        return self.Helper(1, 0, 1, k, dp)

    def Helper(self, i, jump, down, k, dp):

        key = str(i) + ' ' + str(jump) + ' ' + str(down)

        t = 0
        if i == k:
            t += 1

        if i > k+1:
            return 0

        if key in dp.keys():
            return dp[key]

        ans1 = 0
        if down:
            ans1 = self.Helper(i-1, jump, 0, k, dp)

        ans2 = self.Helper(i+(1<<jump), jump+1, 1, k, dp)

        dp[key] = ans1 + ans2 + t
        return dp[key]
