    int waysToReachStair(int k) {
        // cout << c[30][15] << endl; 

        if (k == 0) return 2;
        if (k == 1) return 4;
        vector<vector<int>> c(105, vector<int>(105));
        for (int n=0;n<=30;++n) c[n][0] = 1;

        for (int n=1;n<=30;++n) {
            for (int m=1;m<=n;++m) {
                if (m == 1) c[n][m] = n;
                else if (m == n) c[n][m] = 1;
                else c[n][m] = c[n-1][m-1] + c[n-1][m];
            }
        }

        k = k-1;
        int j = 1;
        int sum = 0;
        int d = 0;
        int ans = 0;

        while (true) {
            sum += j;
            d++;
            j*=2; 
            if (sum < k) continue;
            if (sum - k > d+1) break;
            //int diff = (d+1)-(sum-k);
            int m = sum-k;
            ans += c[d+1][m];
        }

        return ans;
    }
};
