class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def fun(pos, jump, last):
            if pos > k + 1:
                return 0
            res = 0
            if pos == k:
                res = 1
            if last == 0 and pos > 0:
                res = res + fun(pos - 1, jump, 1)
            return res + fun(pos + 2**jump, jump + 1, 0)
        return fun(1, 0, 0)
