class Solution:
    def waysToReachStair(self, k: int) -> int:
        #want to go from stair 0 to stair k
        #start off with an initial value called jump
        #she has two options for moves:
            #a. go down a stair e.g. go from i to i-1
            #b. go to i + 2^jump stair and jump becomes jump+1
            #if at k, can still go back to k

        @cache
        def dp(i,jump,wentDown):
            res = 0
            if i - k > 1:
                return 0

            if i == k:
                res = 1

            if wentDown or i == 0:
                #must jump
                return res + dp(i+2**jump, jump+1, False)

            return res + dp(i+2**jump, jump+1, False) + dp(i-1,jump,True)

        return dp(1,0,False)
