#define ll long long
class Solution {
public:
    int waysToReachStair(int k) {
        if(k == 0) return 2;
        if(k == 1) return 4;

        vector<vector<ll>> ncr(33,vector<ll>(33));
        ncr[0][0] = 1;
        for(int n = 1;n<32;n++){
            ncr[n][0] = 1;
            for(int r = 1;r<n;r++){
                ncr[n][r] = ncr[n-1][r-1] + ncr[n-1][r];
            }
            ncr[n][n] = 1;
        }

        ll ans = 0;
        for(int i = 1;i<=30;i++){
            ll tot = (1LL<<i) - 1;
            ll ones = tot - k  + 1;
            if(ones < 0) continue;
            if(ones > i+1) break;
            ans += ncr[i+1][ones];
        }
        return ans;
    }
};
