#define ll long long int
class Solution {
public:
    ll find(ll k,ll cur,ll jump,ll opr,map<pair<ll,pair<ll,ll>>,ll> &dp){
        if(cur-1>k){
            return 0;
        }
        if(dp.count({cur,{jump,opr}})){
            return dp[{cur,{jump,opr}}];
        }
        if(cur==k){
            ll one=0;
            ll two=0;
            one=find(k,cur+pow(2,jump),jump+1,1,dp);
            if(opr==1&&cur!=0){
                two=find(k,cur-1,jump,0,dp);
            }
            return dp[{cur,{jump,opr}}]= 1+one+two;
        }

        ll one=find(k,cur+pow(2,jump),jump+1,1,dp);
        ll two=0;
        if(opr==1&&cur!=0){
            two=find(k,cur-1,jump,0,dp);
        }
        return dp[{cur,{jump,opr}}]= one+two;
    }
    int waysToReachStair(int k) {

         map<pair<ll,pair<ll,ll>>,ll> dp;
         ll ans=find(k,1,0,1,dp);
         return ans;
    }
};
