class Solution:
    def waysToReachStair(self, k: int) -> int:
        dp = Counter({(1, 0, 0): 1})
        ans = 0
        while dp:
            ndp = Counter()
            for (a, b, c), v in dp.items():
                if a > k + 5: continue
                if a == k: ans += v
                if c == 0: ndp[(a - 1, b, 1)] += v
                ndp[(a + 2 ** b, b + 1, 0)] += v
            dp = ndp
        return ans
