class Solution {
public:
    int C[33][33];
    int waysToReachStair(int k) {
        for(int i = 0; i <= 32; ++i) C[i][0] = 1;
        for(int i = 1; i <= 32; ++i){
            for(int j = 1; j <= i; ++j){
                C[i][j] = C[i - 1][j - 1] + C[i - 1][j];
            }
        }
        int ans = 0;
        if(k == 0 || k == 1) ans += 1;
        for(int i = 0; i <= 29; ++i){
            int t = (1 << i + 1) - 1 - (k - 1);
            if(t < 0 || t >= i + 3) continue;
            ans += C[i + 2][t];
        }
        return ans;
    }
};
