class Solution {
public:
    map<vector<int>,int> mp;

    int help(int cur,int i,int j,int f,int k,int jmp){
        if(cur==k){
            if(jmp==0 && cur>0){
                return 4;
            }
            if(jmp==0 && cur==0){
                return 2;
            }
            if(jmp==1 && cur>0){
                return 2;
            }
            return 1;
        }
        if(cur>k+1){
            return 0;
        }
        if(mp.find({i,j,f})!=mp.end()){
            return mp[{i,j,f}];
        }
        int ans=help(cur+(1<<jmp),i,j+1,0,k,jmp+1);
        if(f==0 && cur>0){
            ans+=help(cur-1,i+1,j,1,k,jmp);
        }
        return mp[{i,j,f}]=ans;
    }

    int waysToReachStair(int k) {
        mp.clear();
        return help(1,0,0,0,k,0);
    }
};
