class Solution {
public:
    long long c[40][40];
    int waysToReachStair(int k) {
        c[1][0]=1;
        c[1][1]=1;
        for (int i=2;i<40;i++) {
            c[i][0]=1;
            for (int j=1;j<=i;j++)
                c[i][j]=c[i-1][j]+c[i-1][j-1];
        }
        int res=0;
        int cur=1;
        int cc=1;
        for (int i=0;i<=31;i++) {
            cur+=cc;
            cc=cc*2;
            if (cur>=k) {
                if (cur-(i+2)>k) break;
                int tt=cur-k;
                res+=c[i+2][tt];
            }
        }
        if (k<=1) res++;
        return res;
    }
};
