class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(stair, jump, prev_down):
            ways = 0
            if stair == k:
                ways += 1
            if stair < 0 or stair > 2*k+1:
                return 0

            if stair > 0 and not prev_down:
                ways += dfs(stair - 1, jump, True)

            new_stair = stair + 2**jump
            if new_stair <= 2*k+1:
                ways += dfs(new_stair, jump + 1, False)

            return ways

        return dfs(1, 0, False)
