class Solution {
public:
    typedef long long ll;
    map<ll , map<ll , map<ll , ll>>> mp;
    ll sol(ll i , ll tar , ll us , ll jum){

        if(i > tar + 1){
            return 0;
        }

        if(mp.count(i) && mp[i].count(us) && mp[i][us].count(jum)){
            return mp[i][us][jum];
        }

        ll ans;
        if(i == tar){
            ans = 1;
        }
        else{
            ans = 0;
        }


        if(i != 0 && us != 1){
            ans += sol(i - 1 , tar , 1 , jum);
        }
        return mp[i][us][jum] = ans + sol( i + (1ll<<jum) , tar , 0 , jum + 1);
    }
    int waysToReachStair(int k) {
        int ans = sol(1 , k , 0,0);
        return ans;
    }
};
