class Solution {
public:
    int waysToReachStair(int k) {
        int MX = 30;
        int ans = 0;
        vector<vector<long long>> C(MX+5, vector<long long>(MX+5, 0));
        C[0][0] = 1;
        for(int i = 1; i <= MX+1; i++) {
            C[i][0] = 1;
            for(int j = 1; j <= MX+1; j++) {
                C[i][j] = C[i-1][j] + C[i-1][j-1];
            }
        }
        for(long long jump = 0, down; jump <= MX; jump++) {
            down = ((long long)1<<jump) - k;
            if(down < 0 || down > jump+1) continue;
            ans += C[jump+1][down];
        }
        return ans;
    }
};
