class Solution {
public:
      map<vector<int>,int>mp;
    int f(int stair,int dest,int jump,int flag){

        // if(stair==dest)return 1;
        if(stair>1e9+7)return 0;

        int ans=0;
        if(mp.find({stair,jump,flag})!=mp.end())return mp[{stair,jump,flag}];
        if(stair==dest)ans++;

     if(stair!=0 &&  flag!=1)   ans+=f(stair-1,dest,jump,1);

        ans+=f(stair+pow(2,jump),dest,jump+1,0);

        return mp[{stair,jump,flag}]= ans;






    }
    int waysToReachStair(int k) {

        return f(1,k,0,0);

    }
};
