#define ll long long int
class Solution {
public:
    map<string, ll>dp;
    int waysToReachStair(int k) {
        ll x = 1;
        int j = 0;
        bool flag = false;

        if(k==0)
            return 2;
        return solve(x, j, flag, k);
    }

    int solve(ll x, int j, bool flag, int &k){
        string key = to_string(x) + "_" + to_string(j) + "_" + to_string(flag);
        if(dp.find(key)!=dp.end())
            return dp[key];
        if(x>2*k)
            return 0;
        int Ans = 0;
        if(x==k)
            Ans++;
        Ans += solve(x + (1<<j), j+1, false, k);
        if(!flag && x>0){
            Ans += solve(x-1, j, true, k);
        }
        return dp[key] = Ans;
    }
};
