#define ll long long int
class Solution {
public:
    int waysToReachStair(int k) {
       ll ans=0;
        for (int i=0;i<32;i++){
            int n = i + 1;
            ll sub = (1LL << i) - k; 

            if (sub < 0 || sub > n){
                continue;
            }
            ans+=func(n,sub);
        }
        return ans;
    }

private:
    int func(int n, int k) {
        if (k > n) 
            return 0;
        if (k == 0 || k == n) 
            return 1;
        vector<std::vector<int>>vec(n + 1,vector<int>(k + 1, 0));
        for(int i=0;i<n+1;i++){
            for(int j=0;j<min(i, k)+1;j++){
                if(j == 0 || j == i){
                    vec[i][j] = 1;
                } 
                else{
                    vec[i][j] = vec[i - 1][j - 1] + vec[i - 1][j];
                }
            }
        }
        return vec[n][k];
    }
};
