class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(i, jump, was_down):
            if i < 0 or i > k + 1:
                return 0

            result = int(i == k)
            # jump up
            result += dfs(i + 2**jump, jump + 1, False)
            if not was_down and i > 0:
                result += dfs(i - 1, jump, True)
            return result

        return dfs(1, 0, False)
