#include <iostream>
#define ll long long

using namespace std;

class Solution {
public:
    ll ncr(ll n, ll r) {
        __int128 ret = 1;
        ll x = max(r, n-r);
        ll y = min(r, n-r);
        for (__int128 i=x+1; i<=n; ++i) {
            ret *= i;
        }
        for (__int128 i=1; i<=y; ++i) {
            ret /= i;
        }
        return (ll)ret;
    }
    int waysToReachStair(int k) {
        ll f = 0, s = 1, z;
        if (k == 1 || k == 0) ++f;
        for (int i=0; i<=30; ++i) {
            s += (1LL<<i);
            if (s >= k) {
                z = s - k;
                if (i+2 >= z) f += ncr(i+2, z);
            }
        }
        return f;
    }
};
