class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def count(x, jump, is_prev_down):
            res = 0 if x != k else 1
            # case 1: go down
            if x > 0 and not is_prev_down:
                res += count(x-1, jump, True)
            # case 2:
            jump_dist = 2**jump
            if x < k or jump_dist == 1:
                res += count(x+jump_dist, jump+1, False)
            return res
        return count(1, 0, False)
