class Solution {
public:
    #define ll long long

map<pair<pair<ll, bool>, ll>, ll> mp;

ll func(ll cur, bool prv, ll jump, ll &k)
{
    ll ans = 0;
    if (cur == k) {
        ans++;
    }

    if (cur - 1 > k) {
        return ans;
    }

    if (mp.find({{cur, prv}, jump}) != mp.end()) {
        return mp[{{cur, prv}, jump}];
    }

    // now we do again
    if (cur != 0 and prv == 0) {
        ans += func(cur - 1, 1, jump, k);
    }

    ans += func(cur + (1 << jump), 0, jump + 1, k);

    return mp[{{cur, prv}, jump}] = ans;
}

int waysToReachStair(ll k)
{
    int ans = 0;
    ans = func(1, 0, 0, k);
    return ans;
}
};
