class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(i: int, first: bool, jump: int) -> int:
            if i > k + 1:
                return 0

            ans = 0
            if i == k:
                ans += 1

            # jump 
            ans += dfs(i + (1 << jump), False, jump + 1)
            if not first:
                if i != 0:
                    ans += dfs(i-1, True, jump)
            return ans

        return dfs(1, False, 0)
