class Solution:
    def waysToReachStair(self, k: int) -> int:

        @lru_cache(maxsize = None)
        def h(i, j):
            res = 0
            # print(i, j)

            if i == k:
                res = 1

            if i > k and i - k > 1:
                return 0



            if i > 0:
                ni = i - 1
                if ni == k:
                    res += 1
                res += h(ni + (1 << j), j + 1)

            res += h(i + (1 << j), j + 1)

            return res

        return h(1, 0)
