class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def f(cur, jump):
            if cur > (k + 1):
                return 0

            p = int(cur == k)

            x = cur + (1 << jump)
            if x <= (k + 1):
                p += f(x, jump + 1)

            if cur > 0:
                p += int((cur - 1) == k) + f(cur - 1 + (1 << jump), jump + 1)

            return p

        return f(1, 0)
