#define ll long long
class Solution {
public:
    ll k;
    map<pair<ll,pair<ll,ll>>,ll> m;
    ll f(ll i,ll j,ll last)
    {
        // if(m.find({1,make_pair(1,1)})!=m.end())
        //     return 1;
        if(m.find({i,{j,last}})!=m.end())
            return m[{i,{j,last}}];
        // cout<<i<<" "<<j<<" "<<last<<"\n";
        if(i==k)
        {
            ll ans=1+f(i+(1<<j),j+1,0);
            if(last==0)
                ans+=f(i-1,j,1);
            return m[{i,{j,last}}]=ans;
        }
        if(i==k+1)
        {
            if(last==1)
                return 0;
            return m[{i,{j,last}}]=f(i-1,j,1);
        }
        if(i>k+1||i==-1)
            return 0;
        ll ans=f(i+(1<<j),j+1,0);
        if(last==0)
            ans+=f(i-1,j,1);
        return m[{i,{j,last}}]=ans;
    }
    int waysToReachStair(int K) {
        k=K;
        int ans=f(1,0,0);
        // cout<<k<<" "<<ans<<"\n";
        return ans;
    }
};
