ll inf = 1e9 * 2;

class Solution {
public:
    ll C(int n, int k) {
        double res = 1;
        for (int i = 1; i <= k; ++i) res = res * (n - k + i) / i;
        return (ll)(res + 0.01);
    }

    int waysToReachStair(int k) {
        ll kl = k;
        vll v; ll x = 1;
        v.push_back(-1000);
        while (x < inf) {
            v.push_back(x);
            x *= 2;
        }
        ll ans = 0, sz = v.size();
        for (int i = 1; i < sz; ++i) {
            ll currMin  = v[i] - i, currMax = v[i];
            if (k >= currMin && k <= currMax) {
                int goDown = currMax - k;
                ans += C(i, goDown);
            }
        }
        return ans;
    }
};
