class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        if k == 1:
            return 4
        if k == 2:
            return 4

        @cache
        def howMany(i, j, canBack):
            if i == k:
                return 1
            if i - 1 == k:
                if canBack:
                    return 1
                else:
                    return 0
            if i > k:
                return 0

            res = howMany(i + 2 ** j , j + 1, True)
            if canBack:
                res += howMany(i - 1, j, False)
            return res




        return howMany(1, 0, True)
