class Solution:
    def waysToReachStair(self, k: int) -> int:
        MX = 2 * (k + 1)

        @cache
        def solve(pos, jump, last_used):            
            if pos > MX:
                return 0

            ans = (1 if pos == k else 0)

            if pos > 0 and last_used != 0:
                ans += solve(pos - 1, jump, 0)

            ans += solve(pos + pow(2, jump), jump + 1, 1)

            return ans

        result = solve(1, 0, -1)

        return result
