class Solution(object):
    def waysToReachStair(self, k):
        """
        :type k: int
        :rtype: int
        """
        q=[1]
        res=0
        def comb(x,y):
            r=1
            for i in range(1,x+1):
                r*=i
            for i in range(1,y+1):
                r=r//i
            for i in range(1,x-y+1):
                r=r//i
            return r
        for i in range(32):
            q.append(q[-1]*2)
        for i in range(33):
            #print(res)
            t=q[i]-k
            if t<0 or t>i+1:
                continue
            else:
                res+=comb(i+1,t)
        return res
