class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(pos,jump):
            if pos-1>k:
                return 0
            cur=1 if pos==k else 0
            a=0
            if pos!=0:
                a=dp((pos-1+(1<<jump)),jump+1)
                if pos-1==k:
                    a+=1
            b=dp(pos+(1<<jump),jump+1)
            return cur+a+b
        return dp(1,0)
