class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(i, jump, t):
            output = 0
            if i == k: output += 1
            if i > int(10e9): return 0
            output += dfs(i + pow(2, jump), jump + 1, True)
            if i > 0 and t: output += dfs(i - 1, jump, False)
            return output

        return dfs(1, 0, True)
