class Solution:
    def waysToReachStair(self, k: int) -> int:
        # upstairs, 1, 2, 4, 8 total 2^jump_time
        # downstairs 0 to jump_time + 1 times.

        options = []
        jump_time = 0
        while 2**jump_time <= k + jump_time + 1:
            op_1 = 2**jump_time - k
            if op_1 >= 0:
                options.append([jump_time, op_1])
            jump_time += 1
        res = 0
        for jump, op1 in options:
            res += math.comb(jump+1, op1)
        return res
