class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(jump, idx, used):
            # if idx == k:
            #     print(a)
            if idx > k + 1:
                return 0
            j = dfs(jump + 1, idx + 2**jump, False) # , a + [' J']) 
            b = 0
            if not used:
                b = dfs(jump, idx - 1, True) # , a + [' B'])

            return j + b + (idx == k)

        return int(dfs(0,1,0)) # , []))
