class Solution:
    def waysToReachStair(self, k: int) -> int:
        memo = {}
        def dp(step, j, canBackstep):
            if step > k+1:
                return 0
            elif (step, j, canBackstep) in memo:
                return memo[(step, j, canBackstep)]
            else:
                res = 0 if step != k else 1
                if canBackstep and step > 0:
                    res += dp(step-1, j, False)
                res += dp(step+2**j, j+1, True)
                memo[(step, j, canBackstep)] = res
                return res

        return dp(1, 0, True)
