class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        if k == 1:
            return 4
        #print('target: ', k)
        ret = 0

        for j in range(65):
            i = (1<<(j+1))-k

            if 0 <= i <= j+2:
                #print(i, j)
                ret += comb(j+2, i)
        #print()
        return ret
