class Solution {
public:
    int goal;
    unordered_map<long long, int> cache;
    int f(int stair, bool down, int jump) {
        if(stair > goal + 4) return 0;
        long long key = ((long long)stair << 32) | (jump << 1) | down;
        if(cache.find(key) != cache.end()) return cache[key];
        int res = stair == goal;
        if(down && stair != 0) res += f(stair - 1, false, jump);
        res += f(stair + pow(2, jump), true, jump + 1);
        return cache[key] = res;
    }
    int waysToReachStair(int k) {
        goal = k;
        return f(1, true, 0);
    }
};
