#define ll long long
class Solution {
public:

    map<pair<int,pair<int,int>>,int>mp;

    ll power(int a,int b)
    {
        if(b==0)return 1;
        ll ans = power(a,b/2);
        ans*=ans;
        if(b%2)
        ans=(ans*a);
        return ans;
    }

    int fun(int req, int k,bool f,int jump)
    {

        if(k<0 || k>req+1)return 0;
        int ans = 0;
        if(k==req)ans++;
        if(mp.count({k,{f,jump}}))return mp[{k,{f,jump}}];

            ll x = power(2,jump);

                int op = fun(req,k+x,false,jump+1);
                ans=(ans+op);

        if(!f)
        {
            int op = fun(req,k-1,true,jump);
            ans=(ans+op);
        }
        return mp[{k,{f,jump}}]=ans;
    }

    int waysToReachStair(int k) {
        mp.clear();
        return fun(k,1,0,0);
    }
};
