class Solution:
    def waysToReachStair(self, k: int) -> int:

        @lru_cache(None)
        def inner(curr, jc, pn):
            if curr - k > 1:
                return 0
            ans = []
            if curr == k:
                ans.append(1)
            ans.append(inner(curr+2**jc, jc+1, False))
            if pn == False and curr > 0:
                ans.append(inner(curr-1, jc, True))
            return sum(ans)
        return inner(1, 0, False)
