class Solution {
    int solve(int k, int val, int ud, int jump, unordered_map<string, int> &m)
    {
        // cout << val << " -> " << ud << " -> " << jump << endl;
        string s = to_string(val) + "," + to_string(ud) + "," + to_string(jump);
        if(m.count(s)) return m[s];
        int count = 0;
        if(k == val) count++;
        if((1 << jump) + val <= k + 1)
            count += solve(k, val + (1 << jump), 1, jump + 1, m);
        if(ud == 1 and val - 1 >= 0)
            count += solve(k, val - 1, 0, jump, m);
        return m[s] = count;
    }
public:
    int waysToReachStair(int k) {
        int count = 0;
        unordered_map<string, int> m;
        count += solve(k, 1, 1, 0, m);
        // cout << endl;
        return count;
    }
};
