class Solution:
    def waysToReachStair(self, k: int) -> int:

        fact = {0:1}
        for val in range(1,40):
            fact[val] = fact[val-1]*val

        ans = 0
        back = 0
        while True:
            curr = 1-back
            jump = 0

            while curr < k:
                curr += 2**jump
                jump += 1


            if back > jump + 1:
                break

            if curr == k:
                ans += fact[jump+1] // (fact[jump+1-back]*fact[back])
            #print(back, ans)
            back += 1

        return(ans)
