class Solution:
    def waysToReachStair(self, k: int) -> int:
        factorials = [1] * 33
        for i in range(2, 33):
            factorials[i] = factorials[i - 1] * i

        def nCr(n, r):
            if r > n:
                return 0
            return factorials[n] // (factorials[r] * factorials[n - r])

        curr = 0
        ans = 0

        if k == 1 or k == 0:
            ans += 1

        for i in range(1,33):
            curr = (curr << 1) + 1
            if curr-i > k:
                break
            for j in range(i + 2):
                if 1 + curr - j == k:
                    ans += nCr(i + 1, j)

        return ans
